import { createSlice } from "@reduxjs/toolkit";


const initialState = {
    myFavourites: [{"id":10,"name":"Alan Rails","status":"Dead","species":"Human","type":"Superhuman (Ghost trains summoner)","gender":"Male","origin":{"name":"unknown","url":""},"location":{"name":"Worldender's lair","url":"4"},"image":"https://rickandmortyapi.com/api/character/avatar/10.jpeg","episode":["25"]}, {"id":10,"name":"Alan Rails","status":"Dead","species":"Human","type":"Superhuman (Ghost trains summoner)","gender":"Male","origin":{"name":"unknown","url":""},"location":{"name":"Worldender's lair","url":"4"},"image":"https://rickandmortyapi.com/api/character/avatar/10.jpeg","episode":["25"]}]
}


export const  myFavouritesSlice  = createSlice({
    name: 'myFavourites',
    initialState,
    reducers: {
        addFav: (state, action) => {
            const { character } = action.payload
            state.myFavourites.push(character)
        },
        removeFav: (state, action) => {
            const {id} = action.payload
            state.myFavourites.filter((char) => char.id !== id)
        }
    }
})

export const { addFav, removeFav} = myFavouritesSlice.actions;
export default myFavouritesSlice.reducer
