import style from './App.module.css';
import Cards from './components/Cards/Cards.jsx';
import Nav from './components/Nav/Nav.jsx';
import { useState, useEffect } from 'react';
import axios from 'axios'
import { Route, Routes,  useLocation, useNavigate} from 'react-router-dom';
import About from './components/About/About.jsx';
import Detail from './components/Detail/Detail.jsx';
import Form from './components/Form/Form.jsx';
import MyFavourites from './components/MyFavourites/MyFavourites.jsx';
import useCharacterState from './zustand/charactersState.js';
const API_KEY = "pi-lulosammartino"
const EMAIL = 'pepito@gmail.com'
const PASSWORD = 'Lulo_123'
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

function App() {  
   
   
   const characters = useCharacterState((state) => state.characters)
   const addCharacter = useCharacterState((state) => (state.addCharacter))
   const removeCharacter = useCharacterState((state) => (state.removeCharacter))
   
   
   const [access, setAccess] = useState(true)
   
   let location = useLocation().pathname  
   let navigate = useNavigate()
   
   // const agregarPersonaje = () => {
   //       axios(`https://rym2.up.railway.app/api/character/${idEstado}?key=${API_KEY}`)
   //      .then(({data}) => data.name ? addCharacter(data) : window.alert(`No hay pj con id ${idEstado}`))
   // }

// const onSearch = (id) => { // verifica si el personaje ya se encuentra en la lista
//    console.log('onSearch')
//    if(!id) window.alert('Por favor, introduzca un id') 
//    else {
//       let repetido = false
//       for(let i = 0; i < characters.length; i++){
//        if(characters[i].id == id) repetido = true
//       }
//       !repetido ? agregarPersonaje() : window.alert('El personaje ya se encuentra en la lista')}
// }



const onClose = (id) => { // cierra la card de x personaje
   removeCharacter(id)
}

const getRandom = () => { // genera una card con id random 
   let random = Math.floor(Math.random() * 826)
   axios(`https://rym2.up.railway.app/api/character/${random}?key=${API_KEY}`)
   .then(({data}) => addCharacter(data))
}

const login = (userData) => {// comprueba que las credenciales coincidan con el registro
   if (userData.password === PASSWORD && userData.email === EMAIL) {
      setAccess(true);
      navigate('/home');
   } 
}

const logOut = () => {
   setAccess(false)
}


useEffect(() => {
   !access && navigate('/');
}, [access]);


/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
   
return (
      <div className={style.app}>
      {location !== '/' && <Nav   getRandom={getRandom} logOut={logOut}/>}
         <Routes>
      <Route path='/' element={<Form login={login}></Form>}></Route>      
      <Route path='/about' element={<About></About>}></Route> 
      <Route path='/home' element={<Cards onClose={onClose} />}>  </Route>
      <Route path='/detail/:id' element={<Detail characters={characters}/>}></Route>
      <Route path='/favourites' element={<MyFavourites></MyFavourites>}></Route> 
         </Routes>

         
      </div>
   );
}
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
export default App;
