import { useParams } from "react-router-dom"
import { useState, useEffect } from "react"
import axios from "axios";
import style from './Detail.module.css'
const API_KEY = "pi-lulosammartino"

export default function Detail(characters) {

const [character, setCharacter] = useState({})

let {id} = useParams()



useEffect(() => {
    axios(`https://rym2.up.railway.app/api/character/${id}?key=${API_KEY}`)
    .then(({ data }) => {
            if (data.name) {
                setCharacter({ data })
                console.log(character)
               } else {
                  window.alert(`No hay personajes con id ${id}`);
               }
            }
         );
 }, []);

    return <div className={style.contenedor}>
      
         <h2>{character.data.name}</h2>
         <h2>Status: {character.data.status}</h2>
         <h2>Species: {character.data.species}</h2>
         <h2>Gender: {character.data.gender}</h2>
         <h2>Origin: {character.data.origin}</h2>
         <img className={style.img} src={character.data.image} alt='imagen de x pj'></img>
        
    </div> 
}
