import './App.css';
import Cards from './components/Cards.jsx';
import Nav from './components/Nav.jsx';
import { useState } from 'react';
import axios from 'axios'
const API_KEY = "pi-lulosammartino"
//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
function App() {

const [characters, setCharacters] = useState([{id: 2, name: 'yo'}])
const [idEstado, setId] = useState("")

const onSearch = () => {
   for(const id in characters) {
      if(id == idEstado){
         window.alert("Ese personaje ya esta en la lista")
      } else { 
         axios(`https://rym2.up.railway.app/api/character/${idEstado}?key=${API_KEY}`)
         .then(({data}) => data.name ? setCharacters((oldChars) => [...oldChars, data]) : window.alert(`No hay pj con id =${idEstado}`))
      }
   }
   
}

const handleChange = (e) => {
   setId(e.target.value)
}

const onClose = (id) => {
   Number(id)
   const filtrados = characters.filter((character) => character.id !== id )
   setCharacters(filtrados)
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
   return (
      <div className='App'>
         <Nav onSearch={onSearch} handleChange={handleChange}/>
         <Cards characters={characters} onClose={onClose}/>
         
      </div>
   );
}

export default App;
