import style from "./Card.module.css"
import { Link } from "react-router-dom";
import useFavouritesStore from "../../zustand/favouritesState";
import { useState } from "react";
  
export default function Card({ id , name, gender, status, species, origin, image, onClose}) {

const [isFav, setIsFav] = useState(false)

useEffect(() => {
   myFavorites.forEach((fav) => {
      if (fav.id === props.id) {
         setIsFav(true);
      }
   });
}, [myFavorites]);   
   
const addFav = useFavouritesStore((state) => state.addFav)

const handleFavorite = () => {
   if(!isFav){
      setIsFav(true);
      addFav({
         id,
         name,
         gender,
         status,
         species,
         origin,
         image,
      })
   }else setIsFav(false)

}

   return (
      <div className={style.card} >
         <div className={style.contenedorx}>

            {
   isFav ? (
      <button onClick={handleFavorite}>‚ù§Ô∏è</button>
   ) : (
      <button onClick={handleFavorite}>ü§ç</button>
   )
}
            <button className={style.delete} onClick={() => onClose(id)}><svg  xmlns="http://www.w3.org/2000/svg" class="icon icon-tabler icon-tabler-x" width="24" height="24" viewBox="0 0 24 24" stroke-width="2" stroke="currentColor" fill="none" stroke-linecap="round" stroke-linejoin="round"><path stroke="none" d="M0 0h24v24H0z" fill="none"/><path d="M18 6l-12 12" /><path d="M6 6l12 12" /></svg></button>
          </div>
         
         <h2 >{name}</h2>
       <Link to={`/detail/${id}`} >
         <img className={style.img} src={image} alt='' /> 
      </Link> 
         

         <h2 >Status: {status}</h2>
         <h2>Species: {species}</h2>
         <h2 >Gender: {gender}</h2>
         <h2 >Origin: {origin.name}</h2>
      </div>
   );
}
