import './App.css';
import Cards from './components/Cards.jsx';
import Nav from './components/Nav.jsx';
import { useState } from 'react';
import axios from 'axios'
import { Route, Routes, Link } from 'react-router-dom';
const API_KEY = "pi-lulosammartino"
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
function App() {

const [characters, setCharacters] = useState([])
const [idEstado, setId] = useState("")


const agregarPersonaje = () => { // busca al personaje y lo guarda en el estado
   axios(`https://rym2.up.railway.app/api/character/${idEstado}?key=${API_KEY}`)
         .then(({data}) => data.name ? setCharacters((oldChars) => [...oldChars, data]) : window.alert(`No hay pj con id =${idEstado}`))
}

const onSearch = () => { // verifica si el personaje ya se encuentra en la lista
   if(!idEstado) window.alert('Por favor, introduzca un id') 
   else {let repetido = false
   for(let i = 0; i < characters.length; i++){
      if(characters[i].id == idEstado) repetido = true
   }
   !repetido ? agregarPersonaje() : window.alert('El personaje ya se encuentra en la lista')}
   
}

const handleChange = (e) => { // cambia el estado por el valor del input
   setId(e.target.value)
}

const onClose = (id) => { // cierra la card de x personaje
   Number(id)
   const filtrados = characters.filter((character) => character.id !== id )
   setCharacters(filtrados)
}

const getRandom = () => { // genera una card con id random 
   let random = Math.floor(Math.random() * 826)
   axios(`https://rym2.up.railway.app/api/character/${random}?key=${API_KEY}`)
         .then(({data}) => setCharacters((oldChars) => [...oldChars, data]))
}


/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
   return (
      <div className='App'>
         <Routes>
      <Route path='/' element={<Nav onSearch={onSearch} handleChange={handleChange} getRandom={getRandom} />}>  </Route>  
      <Route path='/home'> <Cards characters={characters} onClose={onClose}/> </Route>   
         </Routes>

         
      </div>
   );
}
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
export default App;
