import {create} from 'zustand'
import axios from 'axios'
const API_KEY = "pi-lulosammartino"

export const useCharacterState = create((set) => ({
    characters: [],
    addCharacter: (payload) => set((state) => { 
        axios(`https://rym2.up.railway.app/api/character/${payload}?key=${API_KEY}`)
        .then(({data}) => data.name ? [state.characters.push(data)] : window.alert(`No hay pj con id ${idEstado}`))
        }),
    removeCharacter: (payload) => set((state) => [state.characters = state.characters.filter((char) => char.id !== Number(payload))])
}))
