import {create} from 'zustand'

const useFavouritesStore = create((set) => ({
    favourites: [{"id":10,"name":"Alan Rails","status":"Dead","species":"Human","type":"Superhuman (Ghost trains summoner)","gender":"Male","origin":{"name":"unknown","url":""},"location":{"name":"Worldender's lair","url":"4"},"image":"https://rickandmortyapi.com/api/character/avatar/10.jpeg","episode":["25"]}],
    addFav: (payload) => set((state) => ([state.push(payload)]))
}))

export default useFavouritesStore
