import { validarMail, validarPassword } from "./validation"
import style from "./Form.module.css"
import { useState } from "react"
///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
export default function Form() {

const [userData, setUserData] = useState({email: '', password: ''})
const [errors, setErrors] = useState({email:false , password:false})

const handleChange = (e) => {
    if(e.target.name == 'email'){
        setUserData({...userData, email: e.target.value})
        validarMail(e.target.value) ? console.log('ðŸ˜Š') : setErrors({...errors, email: true})     
    } 
    if(e.target.name == 'password'){
        setUserData({...userData, password: e.target.value})
        validarPassword(e.target.value)  
    }
}


///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    return ( 
<div className={style.loginbox}>
  <form>
    <div className={style.userbox}>
      <input type="text" name="email" onChange={(e) => {handleChange(e)}}/>
      <label>Email</label>
      {errors.email && <p className={style.errmail}>ingrese un email valido</p>}
    </div>
    <div className={style.userbox}>
      <input type="password" name="password" onChange={(e) => {handleChange(e)}} />
      <label>Password</label>
    </div><center>
    <a href="#">
           SEND
       <span></span>
    </a></center>
  </form>
</div>
    )
}
