import { useState, useEffect } from "react"
import axios from "axios";
import style from './Detail.module.css'
const API_KEY = "pi-lulosammartino"

export default function Detail({characters}) {

const [character, setCharacter] = useState({ })

let {id} = useParams()



useEffect(() => {
   let found = characters.find((e) => e.id == id)
   console.log(found)
   setCharacter({found})
   console.log(character)
 }, [id]);

    return 
      {character && 
      <div className={style.contenedor}>
         <h2>{character.found.name}</h2>
         <h2>Status: {character.found.status}</h2>
         <h2>Species: {character.found.species}</h2>
         <h2>Gender: {character.found.gender}</h2>
         <h2>Origin: {character.found.origin.name}</h2>
         <img className={style.img} src={character.image} alt='imagen de x pj'></img>
    </div> 
         }    
      
}
